import React, { Component} from 'react'
import './personal.less'
import {Popconfirm, message,Button} from 'antd'
import {nanoid} from 'nanoid'
import {getUserInfo} from '../../api'

export default class Personal extends Component{
    state={
        show:false,
        textdata:[]
    }

    async componentDidMount (){
        let id = localStorage.getItem('id') 
        //console.log(id);
        //发送请求
        let result = await getUserInfo(id)
        if(result!==undefined){
            console.log(result);
            const {status,data} = result
            //console.log(status,data);
            if(status===0){
                console.log('普通用户');
                //普通用户只展示自己的文章
                this.setState({textdata:data.textdata})
            }else if(status===2){
                console.log('管理员账户');
                //管理员展示所有文章和所有账户
            }else{
                message.info('查询失败',1)
            }
        }else{
        //没联网
            console.log('没联网');
        }
        
    }
    delete = () => {
        this.setState({show:!this.state.show})
    }
    deleteText = (event) => {
        let deletedate = event.target.parentNode.parentNode.firstChild
        //发送删除请求
    }
    confirm = (e) => {
        console.log(e);
        message.success('Click on Yes');
      }
      
    cancel = (e) => {
        console.log(e);
        message.error('Click on No');
      }
    render(){
        const textdata = this.state.textdata
        const {show} = this.state
        return <div className='personal'>
            personal组件
            <div className="personal_text_div">
                <p>所有文章</p>
                <Button className="_text_div_delete" onClick={this.delete}>编辑</Button>
                    <div className='title_li'>
                        {   
                            textdata.map((a) => {
                                return <li key={nanoid()}>{a.title}
                                </li>
                            })
                        }
                    </div>
                    <div className='title_date'>
                        {
                            textdata.map((a) => {
                                return <li key={nanoid()}>{a.date}
                                {/* {show?<Button 
                                onClick={this.deleteText}
                                size={'small'}
                                style={{float:'right',marginRight:'10px'}}
                                >
                                删除</Button>:null} */}
                                 <Popconfirm
    title="Are you sure to delete this task?"
    onConfirm={confirm}
    onCancel={cancel}
    okText="Yes"
    cancelText="No"
  >
    <a href="#">Delete</a>
  </Popconfirm>,
                                </li>
                            })
                        }
                    </div>
            </div>
         </div>
    }
}
    

