import React, { Component } from 'react'
import RichTextEditor from './rich_text_editor'
import {Link} from 'react-router-dom'
import {Button,Input} from 'antd'
import {PlusOutlined} from '@ant-design/icons';
import './writing.less'

export default class writing extends Component {
    state={
        showAdd:false
    }

    //新建分类的回调
    addSort = () => {
        
        this.setState({showAdd:!this.sshowAdd})
    }

    render() {
        return (
            <div className="writing_div">
                <div className="writing_left">
                    <Link to="/home">
                    <Button className="writing_left_home" type="primary" shape="round"  size='large' ghost='true'>
                        回首页
                    </Button></Link>
                    <span onClick={this.addSort} style={{color:'white', fontSize:"15px"}}><PlusOutlined/>新建分类</span>
                    <div>
                    <Input 
                    style={{backgroundColor:'#595959',marginTop:'10px',marginBottom:'12px',color:'white'}} 
                    placeholder="请输入分类名..." 
                    maxLength="8"
                    bordered= {false}
                    size='large'
                    />
                    {
                        this.state.showAdd?<div>
                            <Button 
                            type="primary" shape="round" size='middle' ghost='true'
                            style={{color:'	 #33cc00',borderColor:'	 #33cc00'}}>
                            提交</Button>
                            <Button style={{color:'#999999',marginLeft:'10px'}} type="link">取消</Button>
                        </div>:null
                    }
                    </div>
                </div>
                {/* <RichTextEditor/> */}
            </div>
        )
    }
}
