import React, { Component } from 'react'
import './personal.less'
import {message,Button} from 'antd'
import {nanoid} from 'nanoid'
import {getUserInfo} from '../../api'

export default class personal extends Component {
    state={
        data:[],
        textdata:[]
    }

    async componentDidMount (){
        let id = localStorage.getItem('id') 
        //console.log(id);
        //发送请求
        let result = await getUserInfo(id)
        if(result!==undefined){
            console.log(result);
            const {status,data} = result
            //console.log(status,data);
            if(status===0){
                console.log('普通用户');
                //普通用户只展示自己的文章
                this.setState({textdata:data.textdata})
            }else if(status===2){
                console.log('管理员账户');
                //管理员展示所有文章和所有账户
            }else{
                message.info('查询失败',1)
            }
        }else{
        //没联网
            console.log('没联网');
        }
        
    }
    
    render() {
        const textdata = this.state.textdata
        return (
            <div className='personal'>
                personal....
                <div className="personal_text_div">
                <p>我的文章</p>
                    {   
                        textdata.map((a) => {
                            return <li onMouseDown key={nanoid()}>{a.title}
                            <Button 
                             size={'small'}
                             style={{float:'right',marginRight:'30px',display:'none'}}
                             
                             >
                                删除</Button>
                            </li>
                        })
                    }
                </div>
            </div>
        )
    }
}
