import React, { Component} from 'react'
import './personal.less'
import { message,Button,Modal} from 'antd'
import {nanoid} from 'nanoid'
import {getUserInfo,deleteText} from '../../api'

export default class Personal extends Component{
    state={
        textdata:[],
        visible: false ,
        deletedate:'',
        userdata:[]
    }

    async componentDidMount (){
        let id = localStorage.getItem('id') 
        //console.log(id);
        //发送请求
        let result = await getUserInfo(id)
        if(result!==undefined){
            //console.log(result);
            const {status,data} = result
           // console.log(status,data);
            if(status===0){
                console.log('普通用户');
                //普通用户只展示自己的文章
                this.setState({textdata:data.textdata})
            }else if(status===2){
                console.log('管理员账户');
                //管理员展示所有文章和所有账户
                this.setState({textdata:data.textdata,userdata:data.userdata})
            }else{
                message.info('查询失败',1)
            }
        }else{
        //没联网
           message.success('网络差',0.5)
        }
        
    }
    handleOk = async e => {
        this.setState({visible: false})
        let deletedate = this.state.deletedate
        let userdate = this.state.deleteuser
        console.log(deletedate)
        //发送删除请求
        let result = await deleteText(deletedate)
        console.log(result);
        if(result!==undefined&&result.status===0){
            message.success('删除成功',0.5)
        }else{message.success('网络差',0.5)}
    }
    handleCancel = e => {
        this.setState({visible: false})
    }
    deleteText = (event) => {
        this.setState({visible: true,})
        let deletedate = event.target.parentNode.parentNode.innerHTML.slice(0,23)
        //.firstChild
        if(deletedate[0]>=2){
            // console.log(deletedate[0]);
            // console.log(deletedate);
            this.setState({deletedate})
        }else{
            console.log('22');
            let deletedate = event.target.parentNode.parentNode.innerHTML.slice(4,27)
            this.setState({deletedate})
        }
    }
    deleteUser = (event) => {
        this.setState({visible: true,})
        let deleteuser = event.target.parentNode.parentNode.innerHTML.slice(0,23)
        //.firstChild
        if(deleteuser[0]>=2){
            // console.log(deletedate[0]);
            console.log(deleteuser);
            this.setState({deleteuser})
        }else{
            console.log('22');
            let deleteuser = event.target.parentNode.parentNode.innerHTML.slice(4,27)
            console.log(deleteuser);
            this.setState({deleteuser})
        }
    }

    render(){
        const textdata = this.state.textdata
        const userdata = this.state.userdata
        return <div className='personal'>
            personal组件
            <div className="personal_text_div">
                <p>所有文章</p>
                {/* <p>发布时间</p> */}
                    <div className='title_li'>
                        {   
                            textdata?textdata.map((a) => {
                                return <li className='text_title_li' key={nanoid()}>{a.title}
                                </li>
                            }):null
                            
                        }
                    </div>
                    <div className='title_date'>
                        {
                            textdata?textdata.map((a) => {
                                return <li className='text_date_li' key={nanoid()}>{a.date}
                               <Button 
                                onClick={this.deleteText}
                                size={'small'}
                                style={{float:'right',marginRight:'10px'}}
                                >
                                删除</Button>
                                <Modal 
                                title="提示" 
                                mask={false}
                                style={{boxShadow:'none'}}
                                visible={this.state.visible} 
                                onOk={this.handleOk} 
                                onCancel={this.handleCancel}
                                cancelText='取消'
                                okText='确定'>
                                <p>确定删除吗</p>
                                </Modal>
                                </li>
                            }):null
                        }
                    </div>
            </div>
            <div>
                <div>所有用户</div>
                <div>
                    <div>
                        {
                            userdata?userdata.map((a) => {
                                return <li key={nanoid()}>{a.username}</li>
                            }):null
                        }  
                    </div>
                    <div>
                        {
                            userdata?userdata.map((a) => {
                                return <li key={nanoid()}>{a.date}
                                <Button 
                                onClick={this.deleteUser}
                                size={'small'}
                                style={{float:'right',marginRight:'10px'}}
                                >
                                删除</Button>
                                <Modal 
                                title="提示" 
                                mask={false}
                                style={{boxShadow:'none'}}
                                visible={this.state.visible} 
                                onOk={this.handleOk} 
                                onCancel={this.handleCancel}
                                cancelText='取消'
                                okText='确定'>
                                <p>确定删除吗</p>
                                </Modal>
                                </li>
                            }):null
                        }
                    </div>
                </div>
            </div>
        </div>
    }
}
