import React, { Component } from 'react'
import {nanoid} from 'nanoid'

import './content.less'
import {text} from '../../config/text'
const dataArr = text
export default class content extends Component {

    dataRef = React.createRef()
    componentDidMount(){
        
    }
    render() {
        console.log(dataArr)
        return (
            <div className="content">
                <div>
                    <ul>
                        {
                          dataArr.map((data) => {
                            return  <li ref={this.dataRef} key={nanoid()} className="content_li">
                                        <h1 className="content_title">{data.title}</h1>
                                        <p className="content_p">{data.text}</p>
                                    </li>    
                          })
                        }
                    </ul>
                </div>
            </div>
        )
    }
}
