import React, { Component } from 'react'
import Header from '../header/header'
import {Input,BackTop,Button,Form,Modal} from 'antd'
import {reqText,updateComment} from '../../api'
import './detail.less'
// import {text} from '../../config/text'
// const dataArr = text
export default class detail extends Component {
    state={
        text:{date:'1111111111'},
        visible: false
    }
    async componentDidMount(){
        const {node_id} = this.props.location.state
        //console.log(node_id); 
        let result = await reqText(node_id)
        //console.log(result);
        this.setState({text:result.data[0]})
    }
    style = {
        height: 40,
        width: 40,
        lineHeight: '40px',
        borderRadius: 4,
        backgroundColor: '#ea6f5a',
        color: '#fff',
        textAlign: 'center',
        fontSize: 14,
    };
    // showModal = () => {
        
    //     this.setState({
    //       visible: true,
    //     });
    //   };
    点ok的回调
      handleOk = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
      };
    //点返回的回调
      handleCancel = e => {
        //console.log(e);
        this.setState({
          visible: false,
        });
      };
    //发布按钮回调
    onFinish =async (values) => {
        // return () => {
            this.setState({visible: true});
        // };
        //当前登录用户的username\
        // let comment = values.comment
        // let username = JSON.parse(localStorage.getItem('user'))
        // //当前文章的_id和评论
        // const {_id} = this.state.text
        // //发送评论的请求
        // let result = await updateComment(_id,username,comment)
        // console.log(result);
      };
    render() {
        // let date = this.state.text.date.slice(0,10)
        // console.log(date);
        const{text} = this.state
        const { TextArea } = Input;
        return (
            
            <div className='div_detail'>
                <Header/>
                <div className="detail">
                    <div className="detail_div">
                    <h1 className="detail_h1">{text.title}</h1> 
                    <h2 className="detail_h2">作者：{text.username}</h2>
                    <h3>{text.date.slice(0,10)}</h3>
                    <span className="detail_span" dangerouslySetInnerHTML={{__html:text.text}}></span>
                    </div>
                    <div className="detail_comment_">
                    <Form
                    //initialValues={{ remember: true }}
                    onFinish={this.onFinish}
                    >
                    <Form.Item
                        name="comment"
                        rules={[{ required: true, message: '请输入你的评论!' }]}
                    >
                    <TextArea className="comment_text" placeholder="写下你的评论..." size={'large'} showCount maxLength={50}/>
                    </Form.Item>
                    <Button 
                    // onClick={this.showModal}
                     htmlType="submit" style={{float:"right"}} type="primary" shape="round" size={'large'}>
                    发布
                    </Button>
                    <Modal
                    title="提示"
                    visible={this.state.visible}
                    onOk={this.handleOk}
                    onCancel={this.handleCancel}
                    >
                    <p>确定发布吗？</p>
                    </Modal>
                    </Form>
                        <span className="detail_comment">评论</span>
                        <p className="detail_comment_value"></p>
                    </div>
                    
                </div>
                <BackTop>
                    <div style={this.style}>UP</div>
                </BackTop>
            </div>
        )
    }
}
