            }else if(status===2){
                console.log('管理员账户');
                //管理员展示所有文章和所有账户
            }else{
                message.info('查询失败',1)
            }
        }else{
        //没联网
            console.log('没联网');
        }
        
    }
    delete = () => {
        this.setState({show:!this.state.show})
    }
    deleteText = (event) => {
        console.log(event.target.parentNode.parentNode);
    }
