import React, { Component } from 'react'
import Header from '../header/header'
import {Input,BackTop,Button} from 'antd'
import {reqText} from '../../api'
import './detail.less'
// import {text} from '../../config/text'
// const dataArr = text
export default class detail extends Component {
    state={
        text:{date:'1111111111'}
    }
    async componentDidMount(){
        const {node_id} = this.props.location.state
        //console.log(node_id); 
        let result = await reqText(node_id)
        //console.log(result);
        this.setState({text:result.data[0]})
    }
    style = {
        height: 40,
        width: 40,
        lineHeight: '40px',
        borderRadius: 4,
        backgroundColor: '#ea6f5a',
        color: '#fff',
        textAlign: 'center',
        fontSize: 14,
    };
    //评论输入框
    onChange = e => {
        console.log('Change:', e.target.value);
    };
    render() {
        // let date = this.state.text.date.slice(0,10)
        // console.log(date);
        const{text} = this.state
        const { TextArea } = Input;
        return (
            
            <div className='div_detail'>
                <Header/>
                <div className="detail">
                    <div className="detail_div">
                    <h1 className="detail_h1">{text.title}</h1> 
                    <h2 className="detail_h2">作者：{text.username}</h2>
                    <h3>{text.date.slice(0,10)}</h3>
                    <span className="detail_span" dangerouslySetInnerHTML={{__html:text.text}}></span>
                    </div>
                    <div className="detail_comment_">
                    <TextArea className="comment_text" placeholder="写下你的评论..." size={'large'} showCount maxLength={50} onChange={this.onChange} />
                    <Button o style={{float:"right"}} type="primary" shape="round" size={'large'}>
                    发布
                    </Button>
                        <span className="detail_comment">评论</span>
                        <p className="detail_comment_value"></p>
                    </div>
                    
                </div>
                <BackTop>
                    <div style={this.style}>UP</div>
                </BackTop>
            </div>
        )
    }
}
