import React, { Component,useState } from 'react'
import {Link} from 'react-router-dom'
import {EditFilled} from '@ant-design/icons'
import {Button,Modal} from 'antd'
import { connect } from 'react-redux'
import {createDeleteUserInfoAction} from '../../redux/actions/login'

import './header.less'

@connect(
    state=>({user:state.userInfo}),
    {
        DeleteUserInfo:createDeleteUserInfoAction
    }
)
class Header extends Component {
    state={
        show:true
    }

    //判断是否登录
    componentDidMount(){
        console.log(this.props.user)
        //{username: "11", id: "606825d602e2c918e49ef9ce"}
        const {id} = this.props.user
        if(id){
            this.setState({show:false})
        }
        
    }
    App = () => {
        const [isModalVisible, setIsModalVisible] = useState(false);
      
        showModal = () => {
          setIsModalVisible(true);
        };
      
        handleOk = () => {
          setIsModalVisible(false);
        };
      
        const handleCancel = () => {
          setIsModalVisible(false);
        };
    }
    
    render() {
        const {show} = this.state
        console.log(this.props.user.username);
        return (
            <header className="header">
                
                    <p>首页</p>
                    {show?<div className='lar'>
                        <Link to="/login">
                            <button className="login">登录</button>
                        </Link>
                        <Link to='/register'>
                            <button className="register">注册</button>
                        </Link>
                        
                        <button className='write'>
                        <EditFilled />写文章
                        </button>
                    </div>:<div className="user_div">
                        <p className="user_div_p">欢迎您 ,
                        <Button  className="user_div_btn" type="link">{this.props.user.username}</Button>
                        <Button onClick={showModal} className="user_div_btn" type="link">退出登录</Button>
                        </p>
                        <Modal title="Basic Modal" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
                            <p>Some contents...</p>
                            <p>Some contents...</p>
                            <p>Some contents...</p>
                        </Modal>
                    </div>  
                    }
                
                
            </header>
        )
    }
}
export default Header
