import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import qs from 'querystring'
import axios from 'axios'
import { Form, Input, Button} from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import './login.less'

export default class Login extends Component {
    
    render() {
        const onFinish = (values) => {
            console.log('收集的输入内容是: ', values);
            //{username: "12312", password: "111111"}
            const {username,password} = values
            //此处发送登录请求 
            // axios.post(`http://localhost:4000/login?username=${username}&password=${password}`).then(
            //     response => {console.log('登录成功')},
            //     error => {console.log('密码或用户名不正确');}
            // )
            data = 
            axios({
                method: 'post',
                url: 'http://localhost:4000/login',
                data: {
                username: username,
                password: password
                }
            });
        }
    
        return (
            <div className="loginDiv">
                <h1>用户登录</h1>
                <Form
                    name="normal_login"
                    className="login-form"
                    initialValues={{
                        remember: true,
                    }}
                    onFinish={onFinish}
                    >
                    <Form.Item
                        name="username"
                        rules={[
                        {
                            required: true,
                            message: '请输入用户名!',
                        },
                        {
                            min:2,
                            message: '用户名应为2-8位!',
                        },
                        {
                            max:12,
                            message: '密码应为2-8位!',
                        },
                        {
                            pattern:/^[^\s]*$/,
                            message: '用户名不能包含空格'
                        },
                        ]}
                    >
                        <Input size="large" prefix={<UserOutlined className="site-form-item-icon" />} placeholder="用户名" />
                    </Form.Item>
                    <Form.Item
                        name="password"
                        rules={[
                        {
                            required: true,
                            message: '请输入密码!',
                        },
                        {
                            min:6,
                            message: '密码应为6-12位!',
                        },
                        {
                            max:12,
                            message: '密码应为6-12位!',
                        },
                        {
                            pattern:/^[^\s]*$/,
                            message: '密码不能含空格'
                        },
                        {
                            pattern:/^[^\u4e00-\u9fa5]{0,}$/ ,
                            message: '密码不能含中文'
                            
                        }
                        ]}
                    >
                        <Input
                        size="large"
                        prefix={<LockOutlined className="site-form-item-icon" />}
                        type="password"
                        placeholder="密码"
                        />
                    </Form.Item>
                    <Form.Item>
                        <Button type="primary" htmlType="submit" className="login-form-button">
                        登录
                        </Button>
                        
                    </Form.Item>
                </Form>
                <Link to='/home'><span>去首页</span></Link>  
                <Link to='/register'><span className="goregister">去注册</span></Link> 
        </div>
        )
        
    }
}
