import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import {HeartFilled} from '@ant-design/icons'

import './content.less'
import {text} from '../../config/text'
const dataArr = text
export default class content extends Component {

    
    render() {
        console.log(dataArr)
        //console.log(nanoid().slice(0,6));
        return (
            <div className="content">
                <div>
                    <ul>
                        {
                          dataArr.map((data) => {
                            return  <li key={data.node_id} className="content_li">
                                        <Link><h1 className="content_title">{data.title}</h1></Link>
                                        <p className="content_p">{data.text.slice(0,140)+'...'}</p>
                                        <span className="content_span">
                                            {'\u00A0'}{data.id}
                                            {'\u00A0\u00A0\u00A0\u00A0'}<HeartFilled />{'\u00A0'}
                                            {data.star}{'\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0'}{data.time}
                                        </span>
                                        <hr className="content_hr"/>
                                    </li>    
                          })
                        }
                    </ul>
                </div>
            </div>
        )
    }
}
