import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import {reqText} from '../../api'
import {HeartFilled} from '@ant-design/icons'
import {nanoid} from 'nanoid'

import './content.less'
import {message,Button} from 'antd'
import { connect } from 'react-redux'
//import {text} from '../../config/text'
//const dataArr = text
@connect(
    state=>({}),
    {}
)
class Content extends Component {
    state={
        dataArr: []
    }
    async componentDidMount(){
        //请求文章
        let result =await reqText(1)
        //console.log(result.status);
        if(result!==undefined){
            if(result.status===0){
                //console.log('11111');
                this.setState({dataArr:result.data })
            }
        }else{message.error('网络差，请稍后再试！',1)}        
        
    }
    
    render() {
        const{dataArr} = this.state
        //console.log(dataArr)
        //console.log(nanoid().slice(0,6));
        return (
            <div className="content">
                <div>
                    <ul>
                        {
                          dataArr.map((data) => {
                            return  <div key={nanoid()}><li  className="content_li">
                                        <Link to={{pathname:'/detail',state:{node_id:data._id}}}>
                                            <h1 className="content_title">{data.title}</h1>
                                        </Link>
                                        <p className="content_p" dangerouslySetInnerHTML={{__html:data.text.slice(0,140)+'...'}}></p>
                                    </li>   
                                    <span className="content_span">
                                    {'\u00A0'}{data.username}
                                    {'\u00A0\u00A0\u00A0\u00A0'}<HeartFilled />{'\u00A0'}
                                    {data.star}{'\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0'}{data.date.slice(0,10)}
                                </span> 
                                <hr className="content_hr"/>
                                </div>
                          })
                        }
                    </ul>
                </div>
                <Button 
                style={{width:'100%', border:'none',backgroundColor:'#a5a5a5',margin:'15px 0'}} 
                type="primary" shape="round" size={'large'}>
                阅读更多
                </Button>
            </div>
        )
    }
}
export default Content
